x:
, "y" : , "z" : , "m" : , "spatialReference" : {}}
	示例：{"x" : -118.15, "y" : 33.80, "spatialReference" : {"wkid" : 4326}}

2.多点(MultiPoint) 
	{
	  "hasM" : true | false,
	  "hasZ" : true | false,
	  "points" : [[ , , ,  ] , [ , , ,  ], ... ], 
	  "spatialReference" : {}
	}

	示例：{"points" : [ [-97.06138,32.837], [-97.06133,32.836], [-97.06124,32.834], [-97.06127,32.832] ],"spatialReference" : {}}

3、线(Polyline)
	{
	  "hasZ" : true | false,
	  "hasM" : true | false,
	  "paths" : [[[, , , ],...,[, , , ]],
				 ,...,[[, , , ],...,[, , , ]]],
	  "spatialReference" : {}
	}
	示例：{
			"paths" : [ 
			 [ [-97.06138,32.837], [-97.06133,32.836], [-97.06124,32.834], [-97.06127,32.832] ], 
			 [ [-97.06326,32.759], [-97.06298,32.755] ]
			],
			"spatialReference" : {"wkid" : 4326}
		  }

4.面(Polygon)
	{
	  "hasZ" : true | false,
	  "hasM" : true | false,
	  "rings" : [[[, , , ],...,[, , , ]],
				 ,...,[[, , , ],...,[, , , ]]],
	  "spatialReference" : {}
	}
	示例：{
			"rings" : [ 
			 [ [-97.06138,32.837], [-97.06133,32.836], [-97.06124,32.834], [-97.06127,32.832], [-97.06138,32.837] ], 
			 [ [-97.06326,32.759], [-97.06298,32.755], [-97.06153,32.749], [-97.06326,32.759] ]
			],
			"spatialReference" : {"wkid" : 4326}
		  }

4.路由对象(RouteObject,系统自定义)
	var routeObject = {
		geometry:PolylineObject,
		attributes:[
			{
				objectId:number,//对象ID,如果为新添加的顶点，为空
				name:string,//顶点名称，一般为桩号
				nameUsedBefore:string,//修改之前顶点名称
				x:number,//横坐标
				y:number,//纵坐标
				z:number,//高程
				m:number,//里程值
				updatedGeometry:boolean//是否修改了顶点坐标。如果为新添加的，该值为true;
			},
			{
				objectId:number,//对象ID,如果为新添加的顶点，为空
				name:string,//顶点名称，一般为桩号
				nameUsedBefore:string,//修改之前顶点名称
				x:number,//横坐标
				y:number,//纵坐标
				z:number,//高程
				m:number,//里程值
				updatedGeometry:boolean//是否修改了顶点坐标。如果为新添加的，该值为true;		
			}
		]
	}
